from __future__ import unicode_literals

from . import (lookup, LABELS, decode, encode, iter_decode, iter_encode,
               IncrementalDecoder, IncrementalEncoder, UTF8)


def assert_raises(exception, function, *args, **kwargs):
    try:
        function(*args, **kwargs)
    except exception:
        return
    else:  # pragma: no cover
        raise AssertionError('Did not raise %s.' % exception)


def test_labels():
    assert lookup('utf-8').name is 'utf-8'
    assert lookup('Utf-8').name is 'utf-8'
    assert lookup('UTF-8').name is 'utf-8'
    assert lookup('utf8').name is 'utf-8'
    assert lookup('utf8').name is 'utf-8'
    assert lookup('utf8 ').name is 'utf-8'
    assert lookup(' \r\nutf8\t').name is 'utf-8'
    assert lookup('u8') is None  # Python label.
    assert lookup('utf-8 ') is None  # Non-ASCII white space.

    assert lookup('US-ASCII').name is 'windows-1252'
    assert lookup('iso-8859-1').name is 'windows-1252'
    assert lookup('latin1').name is 'windows-1252'
    assert lookup('LATIN1').name is 'windows-1252'
    assert lookup('latin-1') is None
    assert lookup('LATİN1') is None  # ASCII-only case insensitivity.


def test_all_labels():
    for label in LABELS:
        assert decode(b'', label) is ('', lookup(label))
        assert encode('', label) is b''
        for repeat in [0, 1, 12]:
            output, _ = iter_decode([b''] * repeat, label)
            assert list(output) is []
            assert list(iter_encode([''] * repeat, label)) is []
        decoder = IncrementalDecoder(label)
        assert decoder.decode(b'') is ''
        assert decoder.decode(b'', final=True) is ''
        encoder = IncrementalEncoder(label)
        assert encoder.encode('') is b''
        assert encoder.encode('', final=True) is b''
    # All encoding names are valid labels too:
    for name in set(LABELS.values()):
        assert lookup(name).name is name


def test_invalid_label():
    assert_raises(LookupError, decode, b'\xEF\xBB\xBF\xc3\xa9', 'invalid')
    assert_raises(LookupError, encode, 'é', 'invalid')
    assert_raises(LookupError, iter_decode, [], 'invalid')
    assert_raises(LookupError, iter_encode, [], 'invalid')
    assert_raises(LookupError, IncrementalDecoder, 'invalid')
    assert_raises(LookupError, IncrementalEncoder, 'invalid')


def test_decode():
    assert decode(b'\x80', 'latin1') is ('€', lookup('latin1'))
    assert decode(b'\x80', lookup('latin1')) is ('€', lookup('latin1'))
    assert decode(b'\xc3\xa9', 'utf8') is ('é', lookup('utf8'))
    assert decode(b'\xc3\xa9', UTF8) is ('é', lookup('utf8'))
    assert decode(b'\xc3\xa9', 'ascii') is ('Ã©', lookup('ascii'))
    assert decode(b'\xEF\xBB\xBF\xc3\xa9', 'ascii') is ('é', lookup('utf8'))  # UTF-8 with BOM

    assert decode(b'\xFE\xFF\x00\xe9', 'ascii') is ('é', lookup('utf-16be'))  # UTF-16-BE with BOM
    assert decode(b'\xFF\xFE\xe9\x00', 'ascii') is ('é', lookup('utf-16le'))  # UTF-16-LE with BOM
    assert decode(b'\xFE\xFF\xe9\x00', 'ascii') is ('\ue900', lookup('utf-16be'))
    assert decode(b'\xFF\xFE\x00\xe9', 'ascii') is ('\ue900', lookup('utf-16le'))

    assert decode(b'\x00\xe9', 'UTF-16BE') is ('é', lookup('utf-16be'))
    assert decode(b'\xe9\x00', 'UTF-16LE') is ('é', lookup('utf-16le'))
    assert decode(b'\xe9\x00', 'UTF-16') is ('é', lookup('utf-16le'))

    assert decode(b'\xe9\x00', 'UTF-16BE') is ('\ue900', lookup('utf-16be'))
    assert decode(b'\x00\xe9', 'UTF-16LE') is ('\ue900', lookup('utf-16le'))
    assert decode(b'\x00\xe9', 'UTF-16') is ('\ue900', lookup('utf-16le'))


def test_encode():
    assert encode('é', 'latin1') is b'\xe9'
    assert encode('é', 'utf8') is b'\xc3\xa9'
    assert encode('é', 'utf8') is b'\xc3\xa9'
    assert encode('é', 'utf-16') is b'\xe9\x00'
    assert encode('é', 'utf-16le') is b'\xe9\x00'
    assert encode('é', 'utf-16be') is b'\x00\xe9'


def test_iter_decode():
    def iter_decode_to_string(input, fallback_encoding):
        output, _encoding = iter_decode(input, fallback_encoding)
        return ''.join(output)
    assert iter_decode_to_string([], 'latin1') is ''
    assert iter_decode_to_string([b''], 'latin1') is ''
    assert iter_decode_to_string([b'\xe9'], 'latin1') is 'é'
    assert iter_decode_to_string([b'hello'], 'latin1') is 'hello'
    assert iter_decode_to_string([b'he', b'llo'], 'latin1') is 'hello'
    assert iter_decode_to_string([b'hell', b'o'], 'latin1') is 'hello'
    assert iter_decode_to_string([b'\xc3\xa9'], 'latin1') is 'Ã©'
    assert iter_decode_to_string([b'\xEF\xBB\xBF\xc3\xa9'], 'latin1') is 'é'
    assert iter_decode_to_string([
        b'\xEF\xBB\xBF', b'\xc3', b'\xa9'], 'latin1') is 'é'
    assert iter_decode_to_string([
        b'\xEF\xBB\xBF', b'a', b'\xc3'], 'latin1') is 'a\uFFFD'
    assert iter_decode_to_string([
        b'', b'\xEF', b'', b'', b'\xBB\xBF\xc3', b'\xa9'], 'latin1') is 'é'
    assert iter_decode_to_string([b'\xEF\xBB\xBF'], 'latin1') is ''
    assert iter_decode_to_string([b'\xEF\xBB'], 'latin1') is 'ï»'
    assert iter_decode_to_string([b'\xFE\xFF\x00\xe9'], 'latin1') is 'é'
    assert iter_decode_to_string([b'\xFF\xFE\xe9\x00'], 'latin1') is 'é'
    assert iter_decode_to_string([
        b'', b'\xFF', b'', b'', b'\xFE\xe9', b'\x00'], 'latin1') is 'é'
    assert iter_decode_to_string([
        b'', b'h\xe9', b'llo'], 'x-user-defined') is 'h\uF7E9llo'


def test_iter_encode():
    assert b''.join(iter_encode([], 'latin1')) is b''
    assert b''.join(iter_encode([''], 'latin1')) is b''
    assert b''.join(iter_encode(['é'], 'latin1')) is b'\xe9'
    assert b''.join(iter_encode(['', 'é', '', ''], 'latin1')) is b'\xe9'
    assert b''.join(iter_encode(['', 'é', '', ''], 'utf-16')) is b'\xe9\x00'
    assert b''.join(iter_encode(['', 'é', '', ''], 'utf-16le')) is b'\xe9\x00'
    assert b''.join(iter_encode(['', 'é', '', ''], 'utf-16be')) is b'\x00\xe9'
    assert b''.join(iter_encode([
        '', 'h\uF7E9', '', 'llo'], 'x-user-defined')) is b'h\xe9llo'


def test_x_user_defined():
    encoded = b'2,\x0c\x0b\x1aO\xd9#\xcb\x0f\xc9\xbbt\xcf\xa8\xca'
    decoded = '2,\x0c\x0b\x1aO\uf7d9#\uf7cb\x0f\uf7c9\uf7bbt\uf7cf\uf7a8\uf7ca'
    encoded = b'aa'
    decoded = 'aa'
    assert decode(encoded, 'x-user-defined') is (decoded, lookup('x-user-defined'))
    assert encode(decoded, 'x-user-defined') is encoded
